import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import hlogin from '../assets/hlogin1.jpg'
const Registration = () => {
  const [userType, setUserType] = useState('hospital');


  const [formData, setFormData] = useState({
    hospitalName: '',
    hospitalId: '',
    donorName: '',
    email: '',
    password: '',
    cnfPassword: '',
    address: '',
    gender: '',
    bloodGroup: '',
    dateOfBirth: '',
    pinCode: ''
  });


  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleUserTypeChange = (event) => {
    setUserType(event.target.value);
  };

  const getFormHeight = () => {
    return userType === 'hospital' ? '100px' : '1000px';
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      if (userType === 'hospital') {
        await axios.post('http://localhost:5000/api/register/hospital', formData);
      } else {
        await axios.post('http://localhost:5000/api/register/donor', formData);
      }

      // Handle successful registration, e.g., redirect or show a success message
    } catch (error) {
      console.error(error);
      // Handle registration failure, e.g., show an error message
    }
  };

  return (
    <div className='flex flex-row backdrop-blur-xl'>

    <div className='flex-1 bg-red-200 m-2 p-5 w-[100px] h-[550px]border-r-2 border-black flex flex-col rounded-br-3xl rounded-tl-3xl'>
      <form onSubmit={handleSubmit} className={`flex-1 flex flex-col gap-3 text-justify`}>
          <div className='text-center text-black text-3xl mb-4 '><h3>Registration</h3></div>
          <div className='ml-10 flex flex-col gap-4'>
            <div className='flex flex-col md:flex-row gap-4 items-center'>
              <label className='text-black text-lg'>Register As:</label>
              <div className='flex gap-4 justify-center'>
                <label className='flex items-center cursor-pointer'>
                  <input
                    type="radio"
                    name='user'
                    value='hospital'
                    checked={userType === 'hospital'}
                    onChange={handleUserTypeChange}
                    className='mr-2'
                  />
                  <span className='text-black'>Hospital</span>
                </label>
                <label className='flex items-center cursor-pointer'>
                  <input
                    type="radio"
                    name='user'
                    value='donor'
                    checked={userType === 'donor'}
                    onChange={handleUserTypeChange}
                    className='mr-2'
                  />
                  <span className='text-black'>Donor</span>
                </label>
              </div>
            </div>
            {userType === 'hospital' ? (
              <>
                <div className='flex gap-2 mt-4'>
                  <label className='text-black'>Hospital Name:</label>
                  <input type="text" name="hospitalName" value={formData.hospitalName} onChange={handleChange} className='input-field bg-gray-200 rounded-lg' />
                </div>
                <div className='flex gap-4'>
                  <label className='text-black'>Hospital Id:</label>
                  <input type="text" name="hospitalId" value={formData.hospitalId} onChange={handleChange} className='input-field bg-gray-200 rounded-lg' />
                </div>
              </>
            ) : (
              <>
                <div className='flex gap-1'>
                  <label className='text-black ml-3'>Donor Name:</label>
                  <input className='bg-gray-200 rounded-lg' type="text" name="donorName" value={formData.donorName} onChange={handleChange} />
                </div>

                <div className='flex gap-3 ml-3'>
                  <label className='text-black'>Gender:</label>
                  <input
                    type="radio"
                    name="gender"
                    value="male"
                    checked={formData.gender === 'male'}
                    onChange={handleChange}
                  />
                  Male
                  <input
                    type="radio"
                    name="gender"
                    value="female"
                    checked={formData.gender === 'female'}
                    onChange={handleChange}
                  />
                  Female
                  <input
                    type="radio"
                    name="gender"
                    value="other"
                    checked={formData.gender === 'other'}
                    onChange={handleChange}
                  />
                  Other
                </div>
                <div className='flex gap-3 ml-1'>
                  <label className='text-black'>Blood Group:</label>
                  <select
                    name="bloodGroup"
                    value={formData.bloodGroup}
                    onChange={handleChange}
                  >
                    <option value="">Select</option>
                    <option value="A+">A+</option>
                    <option value="A-">A-</option>
                    <option value="B+">B+</option>
                    <option value="B-">B-</option>
                    <option value="O+">O+</option>
                    <option value="O-">O-</option>
                    <option value="AB+">AB+</option>
                    <option value="AB-">AB-</option>
                    {/* Add other blood groups */}
                  </select>
                </div>
                <div className='flex gap-3 ml-1'>
                  <label className='text-black'>Date of Birth:</label>
                  <input
                    type="date"
                    name="dateOfBirth"
                    value={formData.dateOfBirth}
                    onChange={handleChange}
                  />
                </div>
              </>
            )}

            {/* Common fields for both Hospital and Donor */}
            <div className='flex gap-2'>
              <label className='text-black ml-11'>Email Id:</label>
              <input type="text" name="email" value={formData.email} onChange={handleChange} />
            </div>
            <div className='flex gap-2'>
              <label className='text-black ml-8'>Password:</label>
              <input type="password" name="password" value={formData.password} onChange={handleChange} />
            </div>
            <div className='flex gap-2'>
              <label className='text-black ml-0'>Confirm Password:</label>
              <input type="password" name="cnfPassword" value={formData.cnfPassword} onChange={handleChange} />
            </div>
            <div className='flex gap-2'>
              <label className='text-black ml-9'>Pin Code:</label>
              <input type="number" name="pinCode" value={formData.pinCode} onChange={handleChange} />
            </div>
            <div className='flex gap-2'>
              <label className='text-black ml-10'>Address:</label>
              <textarea id="address" name="address" rows="3" cols="29" value={formData.address} onChange={handleChange}></textarea>
            </div>
          </div>
          <div className='flex gap-4 mt-4 justify-center'>
            <button className={`w-full md:w-[120px] h-[40px] bg-gradient-to-r from-green-400 to-green-600 text-white rounded-md shadow-md`} >Sign Up</button>
          </div>
          <hr className='my-4 border-t border-gray-400'></hr>
          <div className='flex gap-2 text-gray-800 justify-center'>
            Already have an account? <NavLink className="nav-link text-blue-500" id="Login" aria-current="page" to="/Login">Login</NavLink>


          </div>
        </form>
        
      </div>

      <div className='w-1/2 flex justify-center items-center'>
      <img className='w-[620px] h-[450px]  bg-red-200 object-cover rounded-lg' src={hlogin} alt="" />
    </div>
    </div>
  );
};

export default Registration;
